package com.kucaroom.mypicture.service.impl;import com.kucaroom.mypicture.domain.CollectPictureLog;import com.kucaroom.mypicture.domain.DownLoadPictureLog;import com.kucaroom.mypicture.service.DownloadPictureLogService;import lombok.extern.slf4j.Slf4j;import org.junit.Assert;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.data.domain.Page;import org.springframework.data.domain.PageRequest;import org.springframework.test.context.junit4.SpringRunner;@Slf4j@SpringBootTest@RunWith(SpringRunner.class)public class DownloadPictureLogServiceImplTest {    @Autowired    private DownloadPictureLogService service;    @Test    public void create() {        DownLoadPictureLog log = service.create(7607,100,1);        Assert.assertNotNull(log);        Assert.assertEquals(log.getUserId(),(Integer) 7607);    }    @Test    public void list() {        PageRequest pageRequest = new PageRequest(0,10);        Page<DownLoadPictureLog> list = service.list(7607,pageRequest);        log.info("内容：{}",list.getContent().toString());        Assert.assertNotNull(list);    }}