package com.kucaroom.mypicture.util;import java.text.ParseException;import java.text.SimpleDateFormat;import java.util.Date;public class DateUtil {    /**     * 获取当前日期     *     * @author yezi     * @return     */    public static String currentDate(){        SimpleDateFormat simpleDateFormat = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");        return simpleDateFormat.format(new Date());    }    /**     * 比较两个日期的大小     *     * @author yezi     * @param source     * @param target     * @return     */    public static Boolean compareDate(String source,String target){        SimpleDateFormat sdf=new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");        try {            Date sourceDate=sdf.parse(source);            Date targetDate=sdf.parse(target);            if (sourceDate.getTime() > targetDate.getTime()) {                return true;            }else{                return false;            }        } catch (ParseException e) {            e.printStackTrace();        }        return false;    }    public static Date addSecond(Integer second){        try {            Date date=new Date();            SimpleDateFormat simpleDateFormat = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");            String datestr = simpleDateFormat.format(new Date(date.getTime() + (long)second * 1000));            Date dateObj = simpleDateFormat.parse(datestr);            return dateObj;        } catch (ParseException e) {            e.printStackTrace();        }        return null;    }}