package com.kucaroom.mypicture.util;import java.util.UUID;public class KeyUtil {    /**     * 生成随机字符串     *     * @author yezi     * @param length     * @return     */    public static String generateKey(Integer length){        return UUID.randomUUID().toString().replace("-", "").substring(0,length);    }    /**     * 生成小程序的allianceKey     * @return     */    public static String key(){        return KeyUtil.generateKey(18);    }}