package com.kucaroom.mypicture.util;/** * 返回数据的工具类 */public class ResponseUtil {    public static ResponseData fail(Integer code, String message){        ResponseData responseData = new ResponseData();        responseData.setCode(code);        responseData.setMessage(message);        responseData.setData(null);        return responseData;    }    /**     * 返回成功     * @param object     * @return     */    public static ResponseData success(Object object){        ResponseData response = new ResponseData();        response.setCode(0);        response.setData(object);        response.setMessage("success");        return  response;    }}